import React, { useEffect, useState } from "react";
import NavigationBar from "../../components/NavigationBar";
import axios, { AxiosResponse } from "axios";

interface WeatherData {
  name: string;
  weather: { description: string }[];
  wind: { speed: number };
  visi: { speed: number };
}

const Home: React.FC = () => {
  const [data, setData] = useState<WeatherData[]>([]);
  const [name, setName] = useState<string>("");

  useEffect(() => {
    if (name) {
      fetchData();
    } else {
      setData([]);
    }
  }, [name]);

  const fetchData = () => {
    axios
      .get<WeatherData>(
        `https://api.openweathermap.org/data/2.5/weather?q=${name}&appid=d022ad0ebfc92ab78aec2593b1607fc8`
      )
      .then((response: AxiosResponse<WeatherData>) => {
        console.log(response);
        setData([response.data]);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  return (
    <>
      <div className="container-fluid" style={{ height: "100vh" }}>
        <NavigationBar />
        <div className="container card p-3 mt-5">
          <div className="row d-flex justify-content-center text-center align-items-center mt-5 mb-5">
            <div className="col-lg-6 col-md-6 col-sm-12">
              <div className="glass2 d-flex justify-content-center align-items-center text-center mt-5">
                <div className="bord">
                  <input
                    className="effect-2"
                    type="text"
                    placeholder="Search City"
                    onChange={(e) => setName(e.target.value)}
                  />
                  <span className="focus-border"></span>
                </div>
              </div>
            </div>
            <div className="col-lg-6 col-md-6 col-sm-12">
              <div className="card p-5">
                {name === "" ? (
                  <div>Please enter a city to check the weather</div>
                ) : (
                  data.map((item, index) => (
                    <div key={index}>
                      <div>City : {item.name}</div>
                      <div>Weather : {item.weather[0].description}</div>
                      <div>Wind Speed : {item.wind.speed}</div>
                      <div>visibility: {item.visibility}</div>
                    </div>
                  ))
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default Home;
