import React, { useEffect, useState } from "react";
import NavigationBar from "../../components/NavigationBar";
import axios from "axios";

interface WeatherData {
  name: string;
  weather: { description: string }[];
  wind: { speed: number };
}

const Home: React.FC = () => {
  const [data, setData] = useState<WeatherData[]>([]);
  const [name, setName] = useState<string>("");

  useEffect(() => {
    if (name) {
      fetchData();
    }
  }, [name]);

  const fetchData = () => {
    axios
      .get<WeatherData[]>(
        `https://api.openweathermap.org/data/2.5/weather?q=${name}&appid=d022ad0ebfc92ab78aec2593b1607fc8`
      )
      .then((response) => {
        console.log(response);
        setData([response.data[0]]);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  return (
    <>
      <div className="container-fluid" style={{ height: "100vh" }}>
        <NavigationBar />
        <div className="container">
          <div className="row d-flex justify-content-center text-center align-items-center mt-5">
            <div className="col-lg-6 col-md-6 col-sm-12">
              <div className="glass2 d-flex justify-content-center align-items-center text-center">
                <div className="bord">
                  <input
                    className="effect-2"
                    type="text"
                    placeholder="Search City"
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                  />
                  <span className="focus-border"></span>
                </div>
              </div>
            </div>
            <div className="col-lg-6 col-md-6 col-sm-12">
            <div className="row">
                     {
                         data.map((item, index) => (
                             <div key={index}>
                                 <div>City : {item.name}</div>
                                 <div>Weather : {item.weather[index].description}</div>
                                 <div>Wind Speed : {item.wind.speed}</div>

                             </div>
                         ))
                     }
                 </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default Home;
