import React, { useState, useEffect } from "react";

const Home2: React.FC = () => {
  const [containerHeight, setContainerHeight] = useState("590px");
  const [weatherBoxDisplay, setWeatherBoxDisplay] = useState("block");
  const [weatherDetailsDisplay, setWeatherDetailsDisplay] = useState("block");
  const [error404Display, setError404Display] = useState("none");
  const [error404Animation, setError404Animation] = useState("");

  useEffect(() => {
    const handleSearchClick = () => {
      const APIKey = "05f4ee67848cc2f2685db6e4c1cf5f3e";
      const cityInput = document.querySelector(
        ".search-box input"
      ) as HTMLInputElement;
      const city = cityInput.value;

      if (city === "") return;

      fetch(
        `https://api.openweathermap.org/data/2.5/weather?q=${city}&units=metric&appid=${APIKey}`
      )
        .then((response) => response.json())
        .then((json) => {
          if (json.cod === "404") {
            setContainerHeight("400px");
            setWeatherBoxDisplay("none");
            setWeatherDetailsDisplay("none");
            setError404Display("block");
            setError404Animation("fadeIn");
            return;
          }

          setError404Display("none");
          setError404Animation("");

          const image = document.querySelector(
            ".weather-box img"
          ) as HTMLImageElement;
          const temperature = document.querySelector(
            ".weather-box .temperature"
          ) as HTMLDivElement;
          const description = document.querySelector(
            ".weather-box .description"
          ) as HTMLDivElement;
          const humidity = document.querySelector(
            ".weather-details .humidity span"
          ) as HTMLSpanElement;
          const wind = document.querySelector(
            ".weather-details .wind span"
          ) as HTMLSpanElement;

          switch (json.weather[0].main) {
            case "Clear":
              image.src = "images/clear.png";
              break;

            case "Rain":
              image.src = "images/rain.png";
              break;

            case "Snow":
              image.src = "images/snow.png";
              break;

            case "Clouds":
              image.src = "images/cloud.png";
              break;

            case "Haze":
              image.src = "images/mist.png";
              break;

            default:
              image.src = "images/cloud.png";
          }

          temperature.innerHTML = `${parseInt(json.main.temp)}<span>°C</span>`;
          description.innerHTML = `${json.weather[0].description}`;
          humidity.innerHTML = `${json.main.humidity}%`;
          wind.innerHTML = `${parseInt(json.wind.speed)}Km/h`;

          setWeatherBoxDisplay("block");
          setWeatherDetailsDisplay("block");
          setContainerHeight("590px");
        });
    };

    const searchButton = document.querySelector(".search-box button");
    searchButton?.addEventListener("click", handleSearchClick);

    return () => {
      searchButton?.removeEventListener("click", handleSearchClick);
    };
  }, []);

  return (
    <div className="your-container-class" style={{ height: containerHeight }}>
      {/* ... other components and elements ... */}
      <div className="search-box">
        <input type="text" />
        <button>Search</button>
      </div>
      <div className="weather-box" style={{ display: weatherBoxDisplay }}>
        {/* ... weather box content ... */}
      </div>
      <div
        className="weather-details"
        style={{ display: weatherDetailsDisplay }}
      >
        {/* ... weather details content ... */}
      </div>
      <div
        className={`not-found ${error404Animation}`}
        style={{ display: error404Display }}
      >
        {/* ... not found content ... */}
      </div>
    </div>
  );
};

export default Home2;
